void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;  
    vec4 fb = texture(iChannel1, uv);

    vec2 offset = fb.rb / 12.0;

    vec4 jc = texture(iChannel0, uv + offset);

    // the worst chroma key ever, shadertoy also doesnt
    // enable blend so i just scale the output by it instead
    float alpha = 0.0;
    float diff = length(vec3(0., 1., 0.) - jc.rgb);

    if (diff > 0.7){
        alpha = jc.a; 
    }   

    fragColor = mix(jc, fb, 0.93);
    fragColor *= alpha;

    vec4 shift = vec4(0.01, 0.00, 0.01, 0.0);
    fragColor = mod(fragColor + (shift * distance(jc, fb)), 1.0);
}
