void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy / iResolution.xy;
    /*Horizontal mirror*/
    //uv.x = 1.0 - uv.x;
    vec4 col = vec4(texture(iChannel0, uv).xyz, 1.0);
    col.r = ((col.b - col.r) / (col.b + col.r));
    /*Output the webcam buffer*/
    fragColor = col;
}
