// maze from https://www.shadertoy.com/view/4scGWf

void mainImage( out vec4 O,  vec2 U )
{
    U += 8.*fwidth(texture(iChannel0, U / iResolution.xy).r) -.5; 
  //U += 8.*length(fwidth(texture(iChannel0, U / iResolution.xy))) -.5;  // variants
  //U += floor(16.*length(fwidth(texture(iChannel0, U / iResolution.xy)))) -.5; 
    O += .1/ fract(   sin(1e5*length (ceil(U/=2.))) < 0.  ? U.x : U.y ) - O; 
}
