vec2 circlePos = vec2(0.5,0.5);
float radius = 0.1;
vec3 FG = vec3(1.0,0.0,0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;

    uv.y *= aspectRatio;
    circlePos.y *= aspectRatio;

    float interp = step(radius, distance(circlePos, uv));
    vec3 CircleOne = interp + FG;

    fragColor = vec4(CircleOne,1.0);
}
