struct start{float xyz; float raydef; vec3 pos; vec2 ray;};
float startTlp(vec3 pos, vec2 ray)
{
    float xyz = dot(pos.xy, ray) - length(fract(sin(normalize(ray)))) - 1.0;
    float raydef = fract(xyz) - max(pos.x, pos.y);
    start rgbr = start(xyz, raydef, pos, ray);
    rgbr.xyz *= 3.0;
    rgbr.raydef *= rgbr.xyz;
    rgbr.pos -= xyz;
    vec4 compare = vec4(rgbr.xyz, rgbr.raydef, length(rgbr.pos), length(rgbr.ray)) - normalize(vec4(pos, max(ray.x, ray.y)));
    vec4 arcs = compare / sin(raydef / xyz - length(pos - normalize(vec3(ray, 1.0))));
    return distance(arcs.x / arcs.y, arcs.z / arcs.w) - distance(arcs.w / arcs.z, arcs.y / arcs.x);
}
vec3 hdView(float lu, float lu2, vec2 pos)
{
    float xy = max(lu, lu2) - length(pos);
    return vec3(smoothstep(pos.x, xy, sin(lu / lu2)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = vec2(max(iResolution.x, iResolution.y) + length(uv));
    vec3 lp = vec3(0.0, 0.0, 3.0) + normalize(vec3(uv, 1.0));
    float lu = startTlp(lp, uv - p * fragCoord);
    float lu2 = startTlp(lp, uv + p / fragCoord);
    vec3 col = hdView(lu - dot(length(uv), 4.35602), lu2 - dot(-4.35602, length(uv)), p - uv);
	fragColor = texture(iChannel0, uv*1.2+0.001) + vec4(col/0.93*uv.y,1.0);
}

