void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = fragCoord / iResolution.xy - 0.5;
    pos.y -= 1.;
	pos.x *= iResolution.x/iResolution.y;
    vec4 textureColor = texture(iChannel0, pos);
    float averageColor = (textureColor.r + textureColor.g + textureColor.b) / 3.0;
    float precent = abs(cos(iTime));
    textureColor.r -= (textureColor.r - averageColor) * precent;  
    textureColor.g -= (textureColor.g - averageColor) * precent;
    textureColor.b -= (textureColor.b - averageColor) * precent;
	fragColor = textureColor;
}
