void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float c = cos( iTime ), s = sin( iTime );	
	vec2 metaballs[4];
	metaballs[0] = vec2( c * 200. + 300., s * 150. + 200.);
	metaballs[1] = vec2( c * 250. + 350., sin( iTime * 0.25 ) * 100. + 200. );
	metaballs[2] = vec2( cos( iTime * 0.33 ) * 300. + 350., sin( iTime * 1.5 ) * 150. + 200.);
	metaballs[3] = iMouse.xy;	

	float metaball_size[4];
	metaball_size[0] = 100.0;
	metaball_size[1] = 300.0;
	metaball_size[2] = 150.0;
	metaball_size[3] = 400.0;

    float tempy = 0.0;
    for( int i = 0; i < 4; i++ )
    {
        float dx = metaballs[i].x - gl_FragCoord.x;
        float dy = metaballs[i].y - gl_FragCoord.y;
    	tempy += metaball_size[i] / pow( dx * dx + dy * dy, 1.0 );
    }

    // this way is a bit fuzzier
    //fragColor = vec4( tempy * 0.8, tempy * 0.3, tempy * 0.3, 1.0 );


    // this way is more solid
	fragColor.r = step( 0.15, tempy );
    fragColor.g = step( 0.3, tempy );
    fragColor.b = step( 0.75, tempy );
}
