void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y*16.0;
    vec2 w  = fragCoord/iResolution.xy;

    float i     = uv.y;
    float fart  = texture(iChannel0,vec2(i ,0.25)).r;
    float fort  = texture(iChannel0,vec2(2000.0/11025.0 ,0.25)).r;

    // Time varying pixel color
    vec3 butt = 0.5 + sin(iTime+uv.y+vec3(1,2,4))
              + 0.5 + sin(iTime+uv.x+vec3(1,2,4))
              + 0.5 + sin(iTime+uv.y+fort*8.0+vec3(1,2,4))
              + 0.5 + sin(iTime+uv.x+vec3(1,2,4));

    // Output to screen
    fragColor = vec4(butt,1.0);
    fragColor = vec4(1.0) - fragColor; 
 //   fragColor = fragColor*8.0-1.0;
 //   fragColor = clamp(fragColor,0.0,1.0);

    vec4 face = vec4(texture(iChannel1,w.xy+fort*1.0+fart*8.0));
    float bland = fragColor.r-fragColor.g-fragColor.b;
    fragColor = mix(fragColor,face,smoothstep(0.5,1.0,bland));
}
