#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy*0.5)/min(iResolution.x, iResolution.y);

    vec3 col = vec3(0.0);
    if (distance(uv, vec2(0.0)) < 0.06) {
    	col = vec3(1.0);
    }
    for (int i=0; i < 5; i++) {
    	float a = -iTime*0.5 + float(i)*M_PI*2.0/5.0;
        float d = 0.15 + sin(iTime)*0.02;
        vec2 p = vec2(cos(a), sin(a))*d;
        if (distance(uv, p) < 0.03) {
        	col = vec3(1.0);
        }
    }

    fragColor = vec4(col, 1.0);
}
