void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float left = float(iFrame * 111 % int(iResolution.x));
    //float left = iTime * 100.0;
    float right = float(iFrame * 3129 % int(iResolution.x));

    float top = float(iFrame * 321 % int(iResolution.y));
    float bottom = float(iFrame * 543 % int(iResolution.y));

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0, uv);

    if(fragCoord.x > left && fragCoord.x < right &&
       fragCoord.y > top && fragCoord.y < bottom)
    {
		col = col + vec4(left,top,bottom,1) * iTimeDelta * 5.0 / iResolution.x;
		//col = col + vec4(iTimeDelta * left / iResolution.x * 10.0,iTimeDelta,iTimeDelta,1);
		//col *= vec4(left / iResolution.x,iTimeDelta,iTimeDelta,1);
    }

    fragColor = col;
}
