/*

great article on how to create palettes

https://iquilezles.org/articles/palettes

*/


const int NUM_WAVES = 5;

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    float wave_input;
    vec3 wave_colour;
   	float wave1;
    vec3 colour = vec3(0.5);
    st.x=8.0*st.x-2.8;
    st.y=5.0*st.y-1.8;
    float combined_colour;
    // the sun body

    float moving_sun = 2.5*abs(sin(0.2*iTime))-0.8;
    float dist_sun = (distance(st,vec2(0.,moving_sun)));
    float sun_colour= (smoothstep(0.1,0.55,1.-dist_sun));


    // glow from the sun
    float glow_size = (dist_sun/5.0);
    float glow_frequence = abs(cos(.2*iTime));
    float glow_intensity = abs(cos(.2*iTime));

    float strength = 0.5;

    float glow =strength*(1.- glow_intensity*glow_frequence*glow_size);

    // combine sun and the glow from the sun

    combined_colour= mix(glow,0.5+0.4*clamp(0.0,1.4,(st.y)),sun_colour);

    colour = pal(combined_colour, vec3(.7,0.8,0.5),vec3(.9,.7,0.1),vec3(.3,.2,.8),vec3(.6,.40,.30) );
    //colour= vec3(combined_colour);




    vec3 grey=vec3(.5);
    for(int j = NUM_WAVES; j >0; j--)
    {
        float i = float(j);
    	float wave_input= .6/(2.5*i)*(sin(+iTime))*(sin(0.5*(i*.432)*st.x*4.+iTime*.8+st.y))-.8/i-1.+1.;
    	wave1 = smoothstep(wave_input-0.1/i,wave_input,st.y);
        wave_input =(1.-st.y/2.5)*(.15*i)*(1.-0.5*abs(cos(.2*iTime)));
        wave_colour = pal(wave_input, vec3(.6,0.7,0.4),vec3(.9,.7,0.1),vec3(.3,.2,1.),vec3(.6,.40,.30) );
        colour = mix(vec3(wave_colour),colour,vec3(wave1));
    }

    //colour = vec3(colour.b);

    fragColor = vec4(colour,1.0);
}
