float wave(vec2 xy, float wavelength, float speed){
    float x = xy.x;
    float y = xy.y;

    return sin(sqrt(x*x + y*y)/wavelength - iTime*speed);
}
vec4 view(float x, float y, float z){
    return vec4((vec3(x,y,z)+1.)/2., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = (2.*fragCoord.xy - iResolution.xy)/100.;
    vec2 x = vec2(1,0);
    vec2 y = vec2(0,1);

    float a = wave(xy - x, 0.5, 1.);
    float b = wave(xy + x, 0.5, 1.);

	fragColor = view(0.,0.,a+b);
}
