float sdAxisAlignedRect(vec2 uv, vec2 tl, vec2 br)
{
  	vec2 d = max(tl - uv, uv - br);
    return length(max(vec2(0.0), d)) + min(0.0, max(d.x, d.y));
}
void mainImage( out vec4 o, in vec2 uvo)
{
    vec2 uv = uvo/iResolution.y;
    uv.x-=iResolution.x/iResolution.y/2.;
    uv.y-=.5;
    float dist = 0.;
    const float h=.45;
    for(float i=h;i>0.;i-=.03)
    {
        float a = (1.-(i/h))*-iTime*.4;
	    float c = cos(a), s = sin(a);
        dist = min(sdAxisAlignedRect(uv*mat2(c, s, -s, c), vec2(-i),vec2(i)), -dist);
    }
    o.rgb = mix(vec3(0.),vec3(.9,.9,.6), 0.77*clamp(1./(clamp(dist, 1./350., 1.)*350.),0.,1.));
	vec2 uvn = uvo.xy / iResolution.xy-.5;
    o -= .07*fract(sin(dot(uvo + iTime,vec2(12.9898,78.233))) * 43758.5453);
    o *= 1.-dot(uvn,uvn*2.1);
    o.a=1.;
}
