vec3 hsv(float h,float s,float v) { //las
	return mix(vec3(1.),clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.),0.,1.),s)*v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy *4.5 - 1.0;
    float c = 1.0;
    vec3 z = vec3(0);
    for (float i=0.; i < 15. ; i++) {
        uv = abs(uv-1.3);
        uv /= clamp(length(uv), 0.27, 0.6);
        vec2 p = vec2(i/15.0+sin(i*iMouse.x+i), sin(iTime*0.2+sin(i)));
		c = min(c, distance(p, uv));
       	z = sin(z+hsv(c, max(uv.x,uv.y), c*0.3)); 
    }

	fragColor = vec4(z*0.5+0.5,1.0);}
