const float numRows = 4.0;
const float numCols = 4.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = vec4(0.0,0.0,0.0,1.0); //default black
    vec4 checker = vec4(1.0,1.0,1.0,1.0); //white for areas to set

    float squareArea = (floor(uv.x * numCols))+(floor(uv.y * numRows)); //gets the area for the number of sqauares desired
																		//by number of rows and cols
    if (mod(squareArea, 2.0) == 0.0) //makes sure only every other square is made white
        fragColor = checker;
}
