// store Mouse.y for each 3 band in O[band] 

void mainImage( out vec4 O, vec2 U) 
{
    vec2 R = iResolution.xy,
         M = iMouse.xy/R;

    O = iFrame==0 
        ? vec4(.47)            // gamma 2.2
        : texture(iChannel0,U/R);  

    if (iMouse.z>0.) 
        O[int(3.*M)] = M.y;
}
