#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float LINE_WIDTH=10.0/iResolution.y;
    float DEGREES=iTime*PI/2.0;
    int NUM_SQUARES=100;
    int SQUARE_OFFSET=30;

    fragColor=vec4(0,0,0,1);

    for(int i=1;i<=NUM_SQUARES;i++){
        float deg=DEGREES*(1.0+float(i-SQUARE_OFFSET)/20.0);

        vec2 normalized=(2.0+float(i-SQUARE_OFFSET))*(fragCoord.xy/iResolution.xy) - ((2.0+float(i-SQUARE_OFFSET))/2.0);
    	vec2 rCoord=vec2(
	        cos(deg)*normalized.x+sin(deg)*normalized.y,
	    	-sin(deg)*normalized.x+cos(deg)*normalized.y
	    );

	    //weird coordinate system used for math. goes 0 -> 1 -> 0
	    vec2 sNormalized=abs(abs(rCoord)-1.0);

        if(abs(abs(rCoord.y)-sNormalized.x)< LINE_WIDTH && rCoord.x>=-1.0 && rCoord.x<=1.0 ){
	        fragColor=vec4(1,1,1,1);
        	break;
    	}
    }


}
