#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=3.*t;
	for(int i=0;i<3;i++) {
		vec2 uv,p=fragCoord.xy/r;
        uv=p;
		p-=.5;
		p.x*=r.x/r.y;
        z+=2.1;
		l=length(p);
        float h = 3.*log(l);

        //digital
		//c[i]=((sin(h-z)+1.)/2.>.9)?1.:0.;

        //analog
        c[i]=(sin(h-z)+1.)/2.;
	}
	fragColor=vec4(c,t);
}
