#define time iTime * 0.5

vec3 color = vec3(.07,0.02,0.1);

//vec2 vMouse = vec2(iMouse.xy);

float hash21(in vec2 n) {
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

mat2 makem2(in float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2( c, -s, s, c);
}

float noise( in vec2 x ){
    return texture(iChannel0, x * .01).x;
}

vec2 gradn(in vec2 p)
{
    float ep = .09;
    float gradx = noise(vec2(p.x+ep,p.y))-noise(vec2(p.x-ep,p.y));
    float grady = noise(vec2(p.x,p.y+ep))-noise(vec2(p.x,p.y-ep));
    return vec2(gradx,grady);
}

float flow(in vec2 p)
{
    float z = 2.;
    float rz = 0.;
    vec2 bp = p;

    for( float i = 1.; i < 7.; i++ )
    {
        p += time * 0.2;
        bp += time * 0.8;
        vec2 gr = gradn(i * p * .64 + time * 0.6 * (texture(iChannel1, vec2(clamp(p.x, .8, 1.) * 4., clamp(p.y, .8, 1.) * 4.)).x + 0.5));
        gr *= makem2(time * 3. -(0.05 * p.x + 0.03 * p.y) * 70.);
        p += gr * .5;
        rz += (sin(noise(p) * 8.) * 0.5 + 0.5) / z;
        p = mix( bp, p, .45);
        z *= 1.8;
        p *= 20.1;
        bp *= 17.9;
    }
    return rz;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = fragCoord.xy / iResolution.xy - .5; //- (vMouse.xy / 1000.);
    //vMouse = mix(vMouse, iMouse.xy, 0.01);
    p.x *= iResolution.x / iResolution.y;
    p *= 7.8;

    float rz = flow(p);

    vec3 col = color / rz;

    col = pow(col, vec3(1.6));

    fragColor = vec4(col,1.0);
}
