//#define REMOVE_OUTLINE

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;

	vec3 s = texture(iChannel0,uv).xyz;
    float l = length(s-vec3(.047,.63529,.14509));
    if (l > .4) {
        #ifdef REMOVE_OUTLINE
    	fragColor = vec4(mix(vec3(0.),s,(l-.4)*4.),1.);
    	#else
        fragColor = vec4(s,1.);
        #endif
    } else {
        //fragColor = vec4(0.);
        discard;
    }
}
