float time;
vec2 resolution;

const int numBalls = 24;
const float radius = 0.25;

const vec3 lightPos = vec3(4.0, 4.0, 0.0);
vec3 sphereCenter;

bool hasHitSphere = false;
vec3 floorHitPoint;

vec4 getColor(vec3 normal)
{
	vec3 lightBack = normalize(lightPos - sphereCenter);
	float diffuse = dot(normal, lightBack);
	if (diffuse < 0.0) diffuse = 0.0;
	float specular = pow(diffuse, 64.0);
	vec4 color = vec4(vec3(0.25 + 1.0*diffuse + 4.0*specular) * vec3(0.25,1.0,0.25), 1.0);
	return color;
}


float z_fractal = 1.0;

const float xpos = -0.99424;
const float ypos = 0.2975;

const float iter = 16.0;
const float iter2 = iter / 4.0;

vec4 getFractalColor(vec2 tc)
{
	float x0 = mod(0.1*tc.x + 0.9, 2.0);
	float y0 = mod(0.025*(1.0 - tc.y) + 0.7, 1.0);

	float z0_r = 0.0;
	float z0_i = 0.0;
	float z1_r = 0.0;
	float z1_i = 0.0;
	float p_r = (x0 + xpos * z_fractal) / z_fractal;
	float p_i = (y0 + ypos * z_fractal) / z_fractal;
	float d = 0.0;

	float nn = 0.0;
	for (float n=0.0; n<iter; n++)
	{
		z1_r = z0_r * z0_r - z0_i * z0_i + p_r;
		z1_i = 2.0 * z0_r * z0_i + p_i;
		d = sqrt(z1_i * z1_i + z1_r * z1_r);
		z0_r = z1_r;
		z0_i = z1_i;
		if (d > iter2) break;
		nn++;
	}

	float c = (1.0*nn) / iter;
	if (c==1.0) c = 0.0;
	c *= 4.0;
	vec4 color = vec4(1.0*c, 1.0*c, 4.0*c, 0.0);
	return color;
}

vec4 getSphereColor(vec3 pos, vec3 dir, float index)
{
	vec4 color = vec4(0.0);

	float yy = -0.4 + sin(index * 0.5);

	vec3 startCenter = vec3(-2.0, yy, 4.0);
	vec3 endCenter = vec3(4.0, yy, 32.0);
	sphereCenter = startCenter + (endCenter - startCenter) * (index / float(numBalls));

	hasHitSphere = false;

	pos -= sphereCenter;

	float b = 2.0 * dot(pos,dir);
	float c = dot(pos,pos) - radius;

	float discr = b*b - 4.0*c;
	if (discr >= 0.0)
	{
		vec2 t = vec2(-b+discr, -b-discr) / 2.0;
		vec3 p = pos + min(t.x,t.y) * dir;
		floorHitPoint = p;
		hasHitSphere = (p.y > -0.7-yy);
		if (hasHitSphere)
		{
			color = getColor(normalize(p));
			hasHitSphere = true;
		}
		else
		{
			floorHitPoint = p;
		}
	}
	return color;
}

vec4 getBackgroundColor(vec2 pix)
{
	if (pix.y < 0.0)
	{
		pix.y = -pix.y;
		float v = 1.0 / pix.y;
		float u = pix.x * v;
		vec4 fractalColor = getFractalColor(vec2(u,v));
		vec3 aview = vec3(0.0, 1.0, 0.0);
		vec3 apos = vec3(u, 0.0, v);
        vec3 apos2 = vec3(u+2.0,0.0,v);
		vec3 adir = normalize(aview - apos);
        vec3 nml = vec3(0.0,1.0,0.0);

        vec3 rdir = 2.0 * dot(nml,adir)*nml-adir;

		vec4 reflectionColor = vec4(0.0);
		for (int i=0; i<numBalls; i++)
		{
			reflectionColor += getSphereColor(apos, rdir, float(i) - mod(4.0*time, 1.0));
			if (hasHitSphere) break;
		}

        vec3 lray = normalize(lightPos-apos);
        float  shadowDelta = 1.0;
        for(int i=0;i<numBalls;++i){
            getSphereColor(apos2,lray,float(i) - mod(4.0*time,1.0));
            if(hasHitSphere) shadowDelta = 0.45;
        }

		return (fractalColor + 2.0 * reflectionColor) * pix.y * shadowDelta;
	}
	else
	{
		return vec4(0.3 - pix.y, 0.0, 1.0 * pix.y, 0.0) * 2.0;
	}
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	time = iTime;
	resolution = iResolution.xy;

	vec2 pix = (fragCoord.xy / resolution - vec2(0.5)) * vec2(1.0, resolution.y / resolution.x);
	vec3 pos = vec3(pix,-1.0);
	vec3 dir = normalize(vec3(pix.x, pix.y, 1.0));

	vec4 col = vec4(0.0);
	for (int i=0; i<numBalls; i++)
	{
		col += getSphereColor(pos, dir, float(i) - mod(4.0*time, 2.0));
		if (hasHitSphere) break;
	}
	if (!hasHitSphere)
	{
		col = getBackgroundColor(pix);
	}

	fragColor = col;
}
