#define max_iters 100

#define maxBlurFactor 50.0
#define timeSpeedFactor 10.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int squarePixelDim = int(ceil(mod(iTime*timeSpeedFactor, maxBlurFactor)));
    vec2 squareLoc = fragCoord / float(squarePixelDim);
    squareLoc = floor(squareLoc);
    vec4 avgColor = vec4(0.0, 0.0, 0.0, 0.0);
    vec2 upperLeftCorner = fragCoord - 0.5 * vec2(squarePixelDim, squarePixelDim);
    for(int i = 0; i < max_iters; i++)
    {
        if (i >= squarePixelDim)
        {
         	break;   
        }
        for(int j = 0; j < max_iters; j++)
        {
            if (j >= squarePixelDim)
            {
                break;   
            }
            vec2 texCoord = (upperLeftCorner + vec2(i,j)) / iResolution.xy;
            texCoord.y = 1.0 - texCoord.y;
            avgColor += texture(iChannel0, vec2(texCoord.x, texCoord.y));
        }
    }

    avgColor = avgColor / float(squarePixelDim*squarePixelDim);

    fragColor = avgColor;
}
