vec2 p(in vec2 z, in vec2 c) {
    return vec2(z.x*z.x - z.y*z.y + c.x, 2.0*(z.x*z.y)+c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c;
 	if(iMouse.z > 0.5)
		c = 4.0 * (fragCoord.xy / iResolution.xy - 0.5);
    else
        c = (0.009 *  fragCoord.xy / iResolution.xy - vec2(-0.3, 0.4900));


    vec2 zn = vec2(0.0,0.0);
    float result = 1.0;

    for (float i = 0.0; i < 800.0; i++) {
        zn = p(zn, c);
        if (dot(zn,zn) > 4.0) {
            result = i*0.005;
        	break;
    	}
    }
    fragColor = vec4(result,result,result,1.0);
}
