void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.x;
    float t = iTime * 5.0;
    uv *= mat2(cos(t),sin(t),-sin(t),cos(t)); // image rotation
	float theta = (atan(uv.y,uv.x) + 3.14)/6.28;
    float dist = length(uv)*2.;
    float spirals = 2.;
    float value = theta + dist*spirals;
    value = fract(value);
    //value = (sin(value*6.28)+1.) * 0.5;
    fragColor = vec4(value);
}
