#define PI 3.14159265359
#define EPSILON 1e-6
float periodic(float x, float amp, float freq, float phase, float shift) {
	return cos((x + phase) * freq) * amp + shift;
}
float make01(float x, float waves, float low, float high) {
	return clamp(((x + waves) / (2. * waves)) * high, low, high);
}
float over(float a, float b) {
    return 1. - (1. - a) * (1. - b);
}
vec2 fourier(float g, float f, float t) {
    float exponent = -2. * PI * f * t;
	return g * vec2(cos(exponent), sin(exponent));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv.x -= .4;
    vec3 prevColor = vec3(0.);
    float T = 10.;
    float f = 60. / T;
    vec3 color;
    int slices = 300;
    float ival = (360. / float(slices));
    float t, g, fourier_r, uv_r, angle, diff, max_angle = .05, mix_val;
    float width = make01(periodic(iTime, 1., 1., 0., .01), 1., 0., .5);
    vec2 c, uv_unit;
    vec3 base = vec3(1., 1., 1.);
    vec3 inner = vec3(1., 0., 0.);
    vec3 outer = vec3(1., 1., 0.);
    for(int i = 0; i < slices; ++i) {
        t = radians(iTime + ival * float(i));
        g = clamp((cos(2. * PI * f * t) + 1.) / 4., 0., .5);
        c = fourier(g, f * .3, t);
        fourier_r = length(c);
        uv_unit = uv - vec2(.5);
        uv_r = length(uv_unit);
        angle = acos(dot(uv_unit, c) / (fourier_r * uv_r));
        diff = distance(fourier_r, uv_r);
        mix_val = smoothstep(0., width, diff);
        color = clamp( max( prevColor, base - over( smoothstep(0., max_angle, angle), mix_val ) ), 0., 1.) * mix(inner, outer, clamp(mix_val * 100., 0., 1.));
    	prevColor = color;
    }
    fragColor = vec4(color,1.0);
}
