// fixed mobile by initializing everything.


void mainImage( out vec4 o, in vec2 uvo)
{
    const float h=1.5;
    float ab = .014,
        t=iTime*.6+1e3,
        k=.2,
        a=0.,
        c=0.,
        s=0.;
    vec3 mc = vec3(.35,.45,.9),
    	mc2 = vec3(1.,0.,0.),
        r=vec3(0.),
        d2=vec3(0.),
        e=vec3(0.),
        n=uvo.xyy,
        a2=vec3(0.),
        b=vec3(0.),
        z=vec3(0.),
        g=vec3(0.);
    n.z=t;
    o.xyz=iResolution;
    vec2 uv = uvo/o.y,
        uvn = uvo.xy / o.xy-.5,
        g2=vec2(0.);
    uv.x-=o.x/o.y/2.;
    uv.y-=.5;
    for(float i=h;i>.2;i-=.05)
    {
        a = (1.-(i/h))*t*(k=-k);
        for(int ai=0;ai<3;++ai) {
    		uv *= mat2(c = cos(a), s = sin(a), -s, c);
        	a=ab;
  			o.xy=max(-uv-i,uv-i);
    		d2[ai] = length(max(g2, o.xy)) + min(0., max(o.x, o.y));
        }
        r = min(d2, -r);
    }
    z+=length(uv);
    r = max(min(max(r,-z+.24), z-.2), -z+.18);
    mc2 = mix(mc, mc2, pow(z,g+1.4)*.8);
    b = pow(max(1.-z,0.),g+2.7)*1e3;
    e = 1.-pow(max(g,r), g+.09);
    a2 = clamp(1./(clamp(r, 1./b, g+1.)*b),0.,1.);
    o.rgb = mix(.12*(-z+1.),mc2, e+.5*a2);
   	n = fract(n * vec3(.14,.13,.15));
    n += dot(n, n.yxz+19.);
    o.rgb -=fract((n.xxy + n.yxx)*n.zyx)*.09;
    o *= 1.-dot(uvn,uvn*2.);
}




