void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord.xy / iResolution.xy;//Condensing this into one line
    float zoom = 2.0;
    float limit = 1.0 - 1.0 / zoom;
    float offset_x = iMouse.x / iResolution.x;
    if(offset_x > limit) offset_x = limit;
    float offset_y = iMouse.y / iResolution.y;
    if(offset_y > limit) offset_y = limit;
    xy = vec2(offset_x + xy.x / zoom, offset_y + xy.y / zoom);

    vec4 texColor = texture(iChannel0,xy);
    fragColor = texColor;

}
