// see also colorful version https://www.shadertoy.com/view/4l2yDD
// 1-slope B&W version https://www.shadertoy.com/view/4lSyDm

#define S(v,s)  smoothstep( -2., 2., abs(o.x-v) * R.y - s )

void mainImage( out vec4 o,vec2 u)  {
    vec2 R = iResolution.xy, 
         U = (u+u-R)/R.y;    
    //float l = length(U);
    float l = dot(U,U) ;   

    float m = length(iMouse.xy)<10. ? .9 : iMouse.y/R.y; 
    o = vec4(l); 
    if (U.x<0.) {
        if (l>.3) o -= .5*m*(l-.3); 
        if (l>.6) o -= .5*m*(l-.6); 
    } else 
        if (l>.6) o -= .5*m*(l-.6);

    o = 1.-o;
    if (U.y<0.) o = pow(max(o,0.),vec4(1./2.2)) ;        // bottom: sRGB conversion
}
