#define M_PI 3.1415926535897932384626433832795
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Time multiplier
    float t = 4000.0;
    //Zoom
    float z = 0.01;
    float p = 0.5+0.5*cos(iTime*t+M_PI*z*(fragCoord.x-iResolution.x/2.0)*(fragCoord.y-iResolution.y/2.0));
    fragColor = vec4(p,p,p,1.0);
}
