void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 s = texture(iChannel0, uv).rgb;
    int am = int(iMouse.y*0.05) > 0 ? int(iMouse.y*0.05) : 6;
    float col = smoothstep( 0.1, 0.9, max(max(s.x,s.y),s.z));
    int b =  1+int(col*float(am));
    vec3 c = vec3(1.0);
    ivec2 fc = ivec2(fragCoord);
    if((fc.x+fc.y)%b == 0 || (fc.x-fc.y)%(b) == 0)
        c = vec3(0.0);
	fragColor = vec4(c,1.0);
}
