#define PI 3.1415927
void mainImage( out vec4 O,  vec2 U )
{
    U/=iResolution.xy;
    O = mod(vec4(1,2,3,0)-3.*U.y, 3.); O = min(O,2.-O);     // linear rainbow
  //O = .5+.5*cos(6.283185*(U.y +vec4(0,1,-1,0)/3.));       // 1/j/jÂ² rainbow
  //O = cos(6.283185*(U.y +vec4(0,1,-1,0)/3.));             // variant
    if (U.x>.5) 
        O = pow(max(O,0.),vec4(1./2.2)); // gamma conversion (i.e., to_SRGB).
      //O = sqrt(max(O,0.));             // fair approximation
}
