float df(vec2 uv)
{
	uv *= 5.;
	float a = atan(uv.x, uv.y)/6.28318*5.;
	float d = length( uv) - mix(0.5,3.0,abs(fract(a)-.5));
	return smoothstep(0.,1., (d)+0.01);
}

void mainImage( out vec4 fC, vec2 fG )
{
	vec2 p = fG /= iResolution.y / 10.;

    float d = 9.;

    for(int x=-1;x<=1;x++)
    for(int y=-1;y<=1;y++)
    {	
        p = vec2(x,y);
        vec2 a = sin( iTime + 9. * fract(sin((floor(fG)+p)*mat2(2,5,5,2))));
		p += .5 + .35*a - fract(fG);
        p *= mat2(cos(a.x),-sin(a.x),sin(a.x),cos(a.x))*cos(a.y);
        d = min(d, df(p));
    }

	vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.5 + 0.5*cos(10.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fC = vec4(col,1);
}
