//Created by pthextract in 2016-Dec-23
void mainImage( out vec4 o, vec2 p ) {

 o = vec4(0.0);
 vec3 S = sin(iDate.w+2.*o.xwx), R = iResolution/4.;
    o += dot( normalize( S   ) , 
              normalize( vec3( sqrt(abs( R*R -dot( p -= (R+R+160.*S).xy,p) ).y)  ,p) )
            );
    o -= dot( normalize( S   ) , 
              normalize( vec3( sqrt(abs( R*R -dot( p -= (R+R+160.*0.62*S).xy,p) ).y)  ,p) )
            );

     o = -cos(2.62*o); 
    o+=vec4(1,+.6+sin(iDate.w)*.2,.3,.1);
    o/=2.;
}


