bool intersect(vec3 origin, vec3 ray, float radius, out float t, out vec3 n) {
    vec3 L = -origin;
    float radiusSquared = radius * radius;
    float tca = dot(L, ray);
    if (tca < 0.0) return false;
    float d2 = dot(L, L) - tca * tca;
    if (d2 > radiusSquared) return false;
    float thc = sqrt(radiusSquared - d2);
    t = tca - thc;
    n = normalize(origin + ray * t);
    return true;
}

float fresnel(vec3 ray, vec3 normal) {
    float scale = 0.2;
    float power = 1.5;
    float c = scale*pow(2. + dot(ray, normal), power);
    return clamp(c, 0., 4.);
}

vec3 sky(vec3 d) {
    float t = iTime;
    float f = 64.0;
    float g = t*.5;
    d.xz *= mat2(-cos(g), sin(g), sin(g), cos(g));
    float a = d.x*sin(t/1.) + d.y*cos(t/3.) + d.z*sin(t/5.);
    float v1 = sin(f/3.14*a + t);
    float cx = d.x + .2*sin(t/5.);
    float cy = d.y + .5*cos(t/3.);
    float v2 = sin(sqrt(f*10.0*(cx*cx + cy*cy + 4.)) + t);
    float v3 = sin(f*d.x/3.14 + t/2.);
    float v = v1 + v2 + v3 + sin(t);
    float stripe = 1. - pow(sin(v*6.), 4.) * .1;
    return vec3((v - .1 + sin(t/4.0)*.2), -cos(v-0.2), -sin(v+1.02))*.9 + .16*stripe;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord / iResolution.xy*1.-0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 origin = vec3(sin(iTime/3.)*0.2, sin(iTime/8.)*.2, -2.+sin(iTime/3.6)*0.9);
    vec3 ray = normalize(vec3(uv.xy, 0.4));
    float distort = pow(sin(iTime/3.), 3.)*.09;
    float radius = 1. + sin(7.*(ray.x + iTime/3.0))*distort + cos(11.*(ray.y + iTime/5.))*distort;

    float t;
    vec3 normal;
    if (intersect(origin, ray, radius, t, normal)) {
        float rc = fresnel(ray, normal);
        float vc = pow(abs(normal.z), 10.)*.4;
        vec3 reflectedRay = reflect(ray, normal);
        vec3 refractedRay = refract(ray, normal, 0./0.31);
        vec3 reflectedColor = sky(reflectedRay);
        vec3 refractedColor = sky(refractedRay);
        vec3 internalColor = mix(refractedColor, vec3(2.), vc);
        vec3 lightDirection = normalize(vec3(0, 1, 0));
        float specular = pow(max(0., dot(reflectedRay, lightDirection)), 12.);
        vec3 color = mix(internalColor, reflectedColor, rc) + specular*.5;
        fragColor = vec4(color, 1.);
    }
    else {
        fragColor = vec4(mix(sky(ray), vec3(.7), .2), 1.);
    }
}
