void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // the amount to pixelate
    const float amount = 1000.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy);

    float d = 1.0 / amount;
	float ar = iResolution.x / iResolution.y;
	uv.x = floor( uv.x / d ) * d;
	d = ar / amount;
	uv.y = floor( uv.y / d ) * d;

    vec4 color = texture(iChannel1, uv);
    float average = 0.2126 * color.x + 0.7152 * color.y + 0.0722 * color.z;
    color = vec4(vec3(average), 1);

    if (color.x <= 0.25)
    {
        color = vec4(0.06, 0.22, 0.06, 1);
    }
    else if (color.x > 0.75)
    {
        color = vec4(0.6, 0.74, 0.06, 1);
    }
    else if (color.x > 0.25 && color.x <= 0.5)
    {
        color = vec4(0.19, 0.38, 0.19, 1);
    }
    else
    {
        color = vec4(0.54, 0.67, 0.06, 1);
    }


    fragColor = color;
}
