vec3 COL1 = vec3(1.0,1.0,1.0);
vec3 COL2 = vec3(0.0,0.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    float grid = float(mod(floor(uv.x * 500.0),14.0) < 0.5);
    grid += float(mod(floor(uv.y * 200.0),11.0) < 0.4); 
    grid = float(grid>0.5);

    vec2 uvn = 2.0 * uv - 1.0;
    grid *= 1.0-clamp(0.0,1.0,pow(length(uvn),0.3+0.1*sin(iTime)));
    // aquire frequency
    float fr = 0.1+ texture(iChannel0,vec2(uv.x*0.7,0.25)).x;
    //atenuate
    float i = pow(1.0-abs(uv.y-fr),20.0);
    vec3 col = vec3(0.8,0.7,0.9)*grid+ vec3(i) * mix(COL1,COL2,uv.x);
    fragColor = vec4(col,1.0);
}
