float random(vec3 scale,float seed){return fract(sin(dot(gl_FragCoord.xyz+seed,scale))*43758.5453+seed);}

vec4 boxBlur(sampler2D tex, vec2 uv, vec2 delta){
    vec4 color=vec4(0.0);
	float total=0.0;
	float offset=random(vec3(12.9898,78.233,151.7182),0.0);
	for(float t=-30.0;t<=30.0;t++){
		float percent=(t+offset-0.5)/30.0;
		float weight=1.0-abs(percent);
		vec4 bsample=texture(iChannel0,uv+delta*percent);
		bsample.rgb*=bsample.a;
		color+=bsample*weight;
		total+=weight;
	}

	color=color/total;
	color.rgb/=color.a+0.00001;

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float angle = iTime *0.5 ;
    float amount = sin(iTime * 0.5) * 0.1;

    vec2 delta = vec2(cos(angle) * amount, sin(angle) * amount);

    vec4 c = boxBlur(iChannel0, uv, delta);

    fragColor = c;

}
