//static and random color generation - shmish

float random (vec2 rndpos) {
    return fract(sin(dot(rndpos.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 display = fragCoord.xy;
    display.x = display.x/iResolution.x;
    display.y = display.y/iResolution.y;
    float time = iTime*0.01;
    float timestatic = iTime*5.0;
    float pixelscale = 0.1;
    float xpos = pixelscale;
    float xneg = 0.00;
    float ypos = pixelscale;
    float yneg = 0.00;
    vec3 randomfactor = vec3(100.0, 100.0, 100.0);
    vec4 color = vec4(0.0, 0.0, 0.0, 1.00);

    // color stuff
    for(int counter = 0; counter <= 99; counter++)
    {
        float rndr = fract(sin(xpos*time)*randomfactor.x);
        float rndg = fract(sin(xpos*time)*randomfactor.y);
        float rndb = fract(sin(xpos*time)*randomfactor.z);

        color.r = rndr;
        color.g = rndg;
        color.b = rndb;

        /*color.r = color.r+0.03;
        color.g = color.r+0.04;
        color.b = color.r+0.04;*/

        if(display.x < xpos && display.x > xneg && display.y < ypos && display.y > yneg)
        {
            fragColor = color;       
        }

        xpos = xpos+pixelscale;
        xneg = xneg+pixelscale;

        if(xpos >=1.1)
        {
            ypos = ypos+pixelscale;
            yneg = yneg+pixelscale;
            xpos = pixelscale;
            xneg = 0.0;
            randomfactor.x = randomfactor.x+25.0;
            randomfactor.y = randomfactor.y+30.0;
            randomfactor.z = randomfactor.z+35.0;
        }
    }
    // base border
    if(display.x < 0.209 || display.x < 1.0 && display.x > 0.79
        || display.y < 0.3 || display.y < 1.0 && display.y > 0.7)
    {
        fragColor = vec4(0.05, 0.05, 0.05, 1.0);
    }

    // background static
    if(display.x < 0.78 && display.x > 0.22 && display.y < 0.68
        && display.y > 0.32)
    {
                vec2 rndposs = fragCoord.xy/iResolution.xy;
                float rnd = random( rndposs*timestatic );
                fragColor = vec4(vec3(rnd),1.0);     
    }
    // top right border
    if(display.x < 0.78 && display.x > 0.765 && display.y < 0.68
        && display.y > 0.32)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
    if(display.x < 0.78 && display.x > 0.22 && display.y < 0.68
        && display.y > 0.655)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
    // bottom left border
    if(display.x < 0.225 && display.x > 0.22 && display.y < 0.68
        && display.y > 0.32)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
    if(display.x < 0.78 && display.x > 0.22 && display.y < 0.325
        && display.y > 0.318)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
