vec2 bipolar(vec2 p,float a){

    float alpha = a*a - dot(p,p);
    float beta = a*a + dot(p,p);
    float gamma= sqrt(alpha*alpha - 4.0*p.y*p.y*a*a);
    float sigma = atan( 2.0*a*p.y ,alpha );
    float tau = 0.5*log((beta + 2.0*a*p.x)/(beta - 2.0*a*p.x));

    return vec2(sigma,tau);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
   	float scale = ( 15.0+0.0*sin(time));
    vec2 p = scale*(fragCoord - 0.5*iResolution.xy) / iResolution.y;

    //rotate
   float rotationrate = 0.1;
    float s = sin(rotationrate*time);
   float c = cos(rotationrate*time);
    p = mat2(c,s,-s,c)*p;

    //float a = min(10.0,2.0*time);
    float a = 5.0;
    vec2 bp = bipolar(p,a);

    float r = 0.1*length(bp);
    float theta = atan(bp.y/bp.x);
   float osc = bp.x + bp.y;
	//vec3 color = vec3((0.5 + 0.5*cos(50.0*theta + 5.0*time)));
    vec3 color = vec3(sin(15.0*osc + 10.0*time), r,0.5 + 0.5*sin(theta));

 	fragColor  = vec4(color,1.0);
}
