#define M_PI 3.141592653589793

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p1 = vec2(.5+sin(iTime*.2)*.1, .5+sin(iTime*.22)*.11);
    vec2 p2 = vec2(.5+sin(iTime*1.48)*.12, .5+cos(iTime*2.52)*.13)*(.5 + sin(iTime*.33)*.3);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x-vec2(0.,(iResolution.y-iResolution.x)/iResolution.x*.5);

    float d1 = distance(uv, p1);
    float d2 = distance(uv, p2);

    // Time varying pixel color
    float d = min(d1,d2)/max(d1,d2)*50.;

    // Output to screen
    fragColor = vec4(sin(d),sin(d+M_PI*2./3.),sin(d+M_PI*4./3.),1.0);
}
