void mainImage( out vec4 O, vec2 U )
{
    U = U*.6/iResolution.y - .27;
    U = fract( U*mat2(1,-1,1,1) ) -.5;
    O +=  8.*( min( min(abs(U.x),abs(U.y)) , 
                                    min(abs(U.x+U.y),abs(U.x-U.y))*.7 )) -O ;

    // draw isovalues in red.
    O +=  .2* step(abs(.5+.49*sin(iDate.w)-O.x) , .02 ) *vec4(1,-1,-1,1);
}
