#define PI 3.14159
#define SPEED .15
#define TRAIL 1.0

float point( vec2 xy, vec2 c, float r ){
    return  1.0 - smoothstep ( 0.0, r, length( xy - c ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = 2.0*fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    vec3 colour = vec3(.55);
    for( float f = .1; f < TRAIL; f+=.02 ){
        float t = PI*(2.0*fract(SPEED*(iTime-f))-1.);
        float x = t * sin( PI * sin(t) / t ); 
        float y = abs(t) * cos( PI * sin(t) / t );
        colour -= vec3( point( uv, vec2( .75*x, -.4*(y-1.0) ), .075*(TRAIL-f) ) );
    }
    fragColor = vec4( colour, 1.0 );
}
