#define center (iResolution.xy / 2.0)
#define PI 4.0 * atan(1.0)

vec2 rect_to_polar(vec2 rect) {
    float r = length(rect);
    float theta = atan(rect.y, rect.x);
    if (theta < 0.0)
        theta += 2.0 * PI;
    return vec2(r, theta);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - center) / iResolution.y;

    //switch to polar coordinates
    vec2 polar = rect_to_polar(uv);

    for (int i = 0; i < 3; i++) {
        float index = float(i) + 1.0;

   		//Make some waves
    	float wave = 0.05 * sin(20.0 * polar.y + index * 2.0 * iTime);
   		float wave_height = 1.0 - abs(polar.x + wave - 0.15 * index);
    	fragColor[i] = smoothstep(0.98, 0.99, wave_height);
    }
    fragColor.a = 1.0;
}
