void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float timescale = iTime*3.5;
    float PI = 3.1415926535897932384626433832795;

    vec2 R = iResolution.xy;
	vec2 relpos = .5*(fragCoord - 0.5*R)/R.y;
    relpos.y += .25;

    float xpos = 0.;
    float ypos = sin(timescale)*0.1+0.25;

    vec4 R4 = vec4(R.x, R.y, R.x, R.y);
    vec4 relMouse = .5*(iMouse - 0.5*R4)/R.y;
    relMouse.y += .25;
    relMouse.w += .25;

    if(length(relMouse.xy - vec2(xpos, ypos)) < 0.02) {
        ypos = relMouse.y;
    }

    float rad = length(relpos - vec2(xpos, ypos))*50.;

    float coils = 12.0;
    float spring = 0.01*sin(coils*2.*PI*(0.5-relpos.y)/(0.5-ypos));

    fragColor = vec4(rad > 1. && (abs(relpos.x-spring) > 0.001 || relpos.y < ypos));
}
