float brightness = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
   	//vec4 newColor = vec4(0.0, 0.0, uv.y, 1.0) + brightness;
	//fragColor = newColor;

    if (uv.x > 0.5)
    {
       fragColor = vec4(0.0, 0.0, 1.0 - uv.y, 1.0) + brightness;
    }
    else
    {
    	fragColor = vec4(uv.y, 0.0, 0.0, 1.0) + brightness;   
    }
}
