#define S smoothstep

void mainImage( out vec4 O, vec2 U )
{
	vec2  V = floor( U *= 10./ iResolution.y );
          U = 2.*fract(U)-1.;
    float g = mod(V.x+V.y, 2.),
          a = atan(U.y,U.x),
          l = length(U),
          d = S(.9,.8,l) ;
	O = cos(3.14*(g
                   +d
                   +S(.1,.0,abs(l-.5)) *step(a,0.) 
                   +S(.3,.2,length(vec2(abs(U.x)-.3,U.y-.4)))
             )) 
        - vec4(0,1.-g,g,0) * d;
}
