void mainImage( out vec4 O,  vec2 U )
{
	O = texture(iChannel0, U / iResolution.xy);
}

#define rnd(U) fract(1e5*sin(dot(U,vec2(1,77.321))))

// fract causes contamination
#define T(i,j) texture(iChannel0, fract( ( U +vec2(i,j) ) / iResolution.xy) )

void mainImage( out vec4 O,  vec2 U )
{
    // try different constants ( especially before the exp ) 
    O = T(0,0)- .4* exp(-3.*(U.x+.0*U.y)/iResolution.x); 
    // O = T(1.05,-0.9)- .25* exp(-3.*(U.x+.0*U.y)/iResolution.x); // public_int variant

    if (O.x > 0.) return;

	O = max( max( max ( T(-1, 1) , T( 0, 1) ),
                  max ( T( 1, 1) , T(-1, 0) ) ),
             max( max ( T( 1, 0) , T(-1,-1) ),
                  max ( T( 0,-1) , T( 1,-1) ) ) ); 

    U -= 9.*iTime;
    if (rnd(U)>.9992) O = vec4(rnd(U+.1),rnd(U+.2),rnd(U+.3),0);
}
