vec2 rot( vec2 p, float l ){
	vec2 r;
	float sl= sin(l), cl= cos(l);
	r.x= cl*p.x - sl*p.y;
	r.y= sl*p.x + cl*p.y;
	return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv= 2.0 * fragCoord / iResolution.xy - vec2(1.0,1.0);
	uv.x *= iResolution.x / iResolution.y;

	vec2 p= uv;

	vec3 r= vec3(0.0);

	float at= atan(p.x,p.y);

	float rad= 0.8;

	rad -= 0.13*abs(cos(at*4.0));

	vec2 ph= rot(p,0.125*3.141592);
	vec2 ph2= rot(p,0.375*3.141592);

	if( length(uv) < rad && 0.02 < min( min( abs(ph.x), abs(ph.y) ), min( abs(ph2.x), abs(ph2.y) ) ) ){
		if( mod( at -0.125*3.141592, 0.5*3.141592) < 0.25*3.141592 )
			r= vec3(0.7);
		else
			r= vec3(0.5,0.05,0.05);
	}

	fragColor = vec4(r, 1.0);
}
