void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
    float C = 1.-U.y/R.y;
	U =5.* ( U+U-R) / R.y;         // normalized coordinates
    U.y = 1.-U.y*2.;                   // swap vertical
    U /= 1.+U.y/10.;                // perspective
    U.y -= iTime;
    U = abs(fract(U)-.50);           // distance to axis
    U = .1/ sqrt(U);                // turn to blured line
    O = (U.x*C+U.y*C*2.) * vec4(.3,.3,1.,0) * C + vec4(.4,0.1,.2,0.) * C; // combine H&V + color * inverted fade
}
