void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec4 c = texture(iChannel0, uv);
    float blur = pow(abs(c.a*5.-mouse.y*5.), 5.);
    vec3 col = textureLod(iChannel0, uv, clamp(blur, 0., 2.)).rgb;
    fragColor = vec4(col, 1.);
}

#define bigness 0.1
#define EPSILON pow(10., -4.)
float align (float diff, float dim){
    if(diff>=dim){
        return diff-dim;
    } else {
        return 0.;
    }
}
float box3D (vec3 boxPos, vec3 boxSize, vec3 pos){
    float n = align(abs(pos.x-boxPos.x), boxSize.x)+align(abs(pos.y-boxPos.y), boxSize.y);
    n+=align(abs(pos.z-boxPos.z), boxSize.z);//just to keep the lines short
    return n;
}
float sphere (vec3 spherePos, vec3 pos, float radius){
    return length(pos-spherePos)-radius;
}
float SDF (vec3 p){
    float rad = bigness*sqrt(2.);
    float d1 = sphere(vec3(0.), p, rad);
    float d2 = box3D(vec3(0.), vec3(bigness), p);
    return max(d1, d2);
}
vec4 trace (vec3 o, vec3 r) {
    float t = 0.;
    vec3 p = o;
    for(int i = 0; i<128; i++){
        p = o+r*t;
        float d = SDF(p);
        t+=d*0.6;
    }
    return vec4(p, t);
}
//Credit to Jamie Wong http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SDF(vec3(p.x + EPSILON, p.y, p.z)) - SDF(vec3(p.x - EPSILON, p.y, p.z)),
        SDF(vec3(p.x, p.y + EPSILON, p.z)) - SDF(vec3(p.x, p.y - EPSILON, p.z)),
        SDF(vec3(p.x, p.y, p.z  + EPSILON)) - SDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}
//made this one myself with some tutorials I found out there on the 
float diffuseLight (vec3 n, vec3 r){
    return dot(n, -r);
}
float specularLight (vec3 n, vec3 r){
    vec3 nr = reflect(r, n);
    float d = dot(nr, -r);
    return pow(max(dot(nr, -r), 0.), 8.);
}
vec3 fractalColor (vec3 p, float iters){
    vec3 c = texture(iChannel1, p).rgb;
    for(float i = 0.; i<iters; i++){
        c+=texture(iChannel1, p*pow(2., i)).rgb*pow(0.8, i);
    }
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 mouse = iMouse.xy/iResolution.xy - vec2(0.5, 0.);
    if(iMouse.xy == vec2(0.))mouse = vec2(0.5, 0.);
    mouse.x*=5.;
    mouse.y*=2.;
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    float groundHeight = 0.;
    float angle = mouse.x;
    float fov = 0.5;
    float camDist = bigness*2.+0.4;
    vec3 camera = vec3(cos(mouse.x)*camDist, 0., sin(mouse.x)*camDist);
    vec3 ray = vec3(-cos(mouse.x+uv.x*fov)*2., uv.y, -sin(mouse.x+uv.x*fov)*2.);
    ray = normalize(ray);

    vec4 v = trace(camera, ray);
    vec3 normal = estimateNormal(v.xyz);
    vec3 col = fractalColor(v.xyz, 5.);//fractalize
    col*=1./length(col);//normalize
    //vec3 randomize = texture(iChannel1, v.xyz*10.).xyz-0.5; 
    //col = texture(iChannel0, estimateNormal(v.xyz)+randomize*0.05).rgb;
    col+=specularLight(normal, ray)*0.1;
    col+=diffuseLight(normal, ray)*0.3;
	float fog = 1./(1.+pow(v.w, 10.)*0.1);
    col*=fog;
    // Output to screen
    fragColor = vec4(clamp(col,0., 1.),length(camera-v.xyz));
}
